/* eslint-disable no-unused-expressions */
import React from 'react';

class Input extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      userFullName: '',
      userJob: '',
      userPhone: '',
      userEmail: '',
      userLinkedin: '',
      userGithub: '',
    };
    this.updateUserInfo = this.updateUserInfo.bind(this);
  }

  updateUserInfo(event) {
    const userInfo = event.target.value;
    const key = event.target.name;
    this.setState({
      [key]: `${userInfo}`,
    });
    this.forceUpdate();
    console.log(this.state);
  }
  render() {
    return (
      <div className="fill-in_item">
        <label className="fill-in_label" htmlFor={this.props.id}>
          {this.props.label}
        </label>
        <input className={`fill-in_input ${this.props.classInput}`} id={this.props.id} type={this.props.type} name={this.props.name} placeholder={this.props.placeholder} minLength={this.props.minLength} pattern={this.props.pattern} actiontoperform={this.updateUserInfo} required />
        {/* <button onClick={this.showingState}>click</button> */}
      </div>
    );
  }
}

export default Input;
